package Loops;

public class ForLoopEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=4;
int y=5;		
    for (int i=0;i<=6;x++) {
    	while(x<y) {
    		System.out.println("*");
    		x++;
    	}
    	for (int j=1;j>=i;y++) {
    		
    		System.out.print("*");
    	}
    	System.out.println("*");
   
	}

}
}
