package operators;

public class ShiftOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=30;
		int c=40;
		System.out.println("Left Shift:a<<2= "+(a<<2));        //10^2*2  10^4=40      
		System.out.println(" right Shift:a>>2= "+(c>>2));       //40^2*2  40^4=160

	}

}
