package operators;

public class ShiftOperatorEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a=100;
      int b=200;
      
      System.out.println(a<<b);           //leftshiftoperator multiplication  100^2*2=100^4=400
      
      System.out.println(a>>b);            //rightshiftoperator  division      200^2*2=200^4=50
	}

}
