package uniqtask;

public class GreatestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100;
		int b=350;
		int c=460;
		if(a>b) {
			System.out.println("a is greater");
		}
		else if(b>c) {
			System.out.println("b is greater");
		}
		else if(c>b){
			System.out.println("c is greater");
		}

	}

}
